(()=>{"use strict";let e=document.querySelector("#user-input"),t=document.querySelector("#search-button"),n=document.querySelector("main");t.addEventListener("click",(function(){(async function(){const t=await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${e.value}&appid=90a2160e298f430b373827c5c8f9aaa2`);return await t.json()})().then((e=>{console.log(e),function(e){var t,a;(t=document.querySelector(".inserted-content"))&&t.parentNode.removeChild(t),n.insertAdjacentHTML("beforeend",`<article class='inserted-content'>\n            <h3 id='weather-condition'>${e.weather[0].main}</h3>\n            <h2 id='temperature'>${a=e.main.temp,Math.ceil(9*(a-273.15)/5+32)}</h2>\n            <h3 id='place-name'>${e.name}</h3>\n        </article>\n        `)}(e)})).catch((e=>console.log(e.message)))}))})();